using System.Collections; using System.Collections.Generic; using UnityEngine; using UnityEngine.UI; using UnityEngine.SceneManagement;  public class Bullet : MonoBehaviour {  	public bool TutorialMode; 	public bool ShootSuccess;  	float speed;  	// Use this for initialization 	void Start () { 		speed = 25f;  		//ShootSuccess = false;  		Scene scene = SceneManager.GetActiveScene(); 		if (scene.name == "Tutorial") { 			TutorialMode = true; 		} else { 			TutorialMode = false; 		}   	}  	// Update is called once per frame 	void Update () {  		// Get the bullet's current position 		Vector2 position = transform.position;  		// Compute the bullet's new position 		position = new Vector2(position.x, position.y + speed * Time.deltaTime);  		// Update the bullet's position  		transform.position = position;  		// this is the top-right position of the screen 		Vector2 max = Camera.main.ViewportToWorldPoint (new Vector2 (1, 1));  		// If the bullet went outside the screen on the top, then destroy the bullet. 		if (transform.position.y > max.y) { 			Destroy (gameObject); 		} 	} 	void OnTriggerEnter2D(Collider2D other) 	{ 		if ((other.gameObject.tag == "BiggerThan") || (other.tag == "BiggerThanExponent")) { 			Destroy (gameObject); 		} 		if ((other.gameObject.tag == "BlueRandom") || (other.tag == "GreenExponent")) { 			Destroy (gameObject); 		} 		if ((other.gameObject.tag == "GreenMultiply") || (other.tag == "GronRandom")) { 			Destroy (gameObject); 		} 		if ((other.gameObject.tag == "OrangeRandom") || (other.tag == "Prim")) { 			Destroy (gameObject); 		} 		if ((other.gameObject.tag == "RedExponent") || (other.tag == "RedMultiply") || (other.tag == "RedPlus")) { 			Destroy (gameObject); 		} 		if ((other.gameObject.tag == "RodRandom") || (other.tag == "SmallerThan") || (other.tag == "GreenPlus")) { 			Destroy (gameObject); 		} 		if ((other.gameObject.tag == "SmallerThanExponent") || (other.tag == "Surprise") || (other.tag == "Reverse") ) { 			Destroy (gameObject); 		}  		if (TutorialMode == true) { 			//ShootSuccess = true;  			GameObject thePlayer = GameObject.Find ("1"); 			TutorialSpawnerScript playerScript = thePlayer.GetComponent<TutorialSpawnerScript> (); 			playerScript.TutorialCompleted = true;    		} 	} } 