using UnityEngine; using System.Collections; using UnityEngine.UI;  public class CameraMovement : MonoBehaviour {  	public float startSpeed = 5f; 	public float acceleration = 3.0f; 	public float maxSpeed = 60.0f;  	public Button SpeedLow; 	public Button SpeedMedium; 	public Button SpeedHigh;  	void Start () {  		startSpeed = PlayerPrefs.GetFloat ("Speed");  		Button lowBtn = SpeedLow.GetComponent<Button>(); 		lowBtn.onClick.AddListener(SetSpeedLow);  		Button medBtn = SpeedMedium.GetComponent<Button>(); 		medBtn.onClick.AddListener(SetSpeedMedium);  		Button hBtn = SpeedHigh.GetComponent<Button>(); 		hBtn.onClick.AddListener(SetSpeedHigh);  	}  	void Update () {    		transform.Translate (0, startSpeed * Time.deltaTime, 0);  		startSpeed += acceleration;  		if (startSpeed > maxSpeed) 			startSpeed = maxSpeed;  	}  	void SetSpeedLow() 	{  		startSpeed = 1.8f; 	}  	void SetSpeedMedium() 	{  		startSpeed = 2.5f; 	}  	void SetSpeedHigh() 	{  		startSpeed = 4; 	}  } 